import React, {Component} from 'react';
import {Table, TableCell, TableRow, TableHead } from '@material-ui/core';
import "ka-table/style.css";
import moment from 'moment';  // 날짜 타입
// import '../css/default.css';
import '../css/Board.css';
import { Link, BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import Record from './Record';
import project from './projcet';


class BoardList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value : '',
            show_update: "none",
            show_delete: "none",
            show_detail: "none"
        }
    }
    // 상세버튼 클릭
    displayShowClick = (e) => {
        this.setState({
            show_detail: "block",
            show_update: "none",
            show_delete: "none"
        });
    } 


    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_detail: "none",
            show_update: "block",
            show_delete: "none" 
        });
    }   

    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_detail: "none",
            show_update: "none",
            show_delete: "block"
        });
    }

    
    render() {
        // let regdate = this.props.writeDate;
        return(
            <Router>
                <tbody class="ka-tbody">
                    <tr class="ka-tr ka-row">
                        <td class="ka-cell">
                            <div class="ka-cell-text">
                                <input type="button" class="btnC" value="상세" onClick={this.displayShowClick}></input>
                            </div>
                        </td>
                        <td class="ka-cell">
                            <div class="ka-cell-text">test1</div>
                        </td>
                        <td class="ka-cell">
                            <div class="ka-cell-text">test2</div>
                        </td>
                        <td class="ka-cell">
                            <div class="ka-cell-text">test3</div>
                        </td>
                        <td class="ka-cell">
                            <div class="ka-cell-text">test4</div>
                        </td>
                    </tr>  
                    {/* <form action="/api/boardUpdate" method="POST"> */}
                    <div className="list" style={{display: this.state.show_detail}} display="block"> 
                        <Table>
                            <TableHead>
                                <TableRow className="TableRow">
                                <TableCell>업무일지내용</TableCell>
                                    <textarea value="김삿갓삿갓입니다"></textarea>
                                </TableRow>
                            </TableHead>
                        </Table>
                        <br></br>
                        <button onClick={this.displayUpdateClick} >수정</button>
                        <button onClick={this.displayDeleteClick}>삭제</button>
                        </div>
                        {/* </form>   */}
                </tbody> 
                </Router>
        )         
    }
}
export default BoardList;
