import React, {Component} from 'react';
import { Input, Table, TableCell, TableRow } from '@material-ui/core';
import moment from 'mement';  // 날짜 타입
import '../css/default.css';

class BoardList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value : '',
            show_update: "none",
            show_delete: "none"
        }
    }

    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_update: "block",
            show_delete: "none" 
        });
    }   

    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_update: "none",
            show_delete: "block"
        });
    }
    render() {
        let regdate = this.props.board_regdate;
        return(
            <div>
                <form action="" method="POST">
                    <input type="hidden" name="board_no" value={this.props.board_no} />
                    <Table>
                        <TableRow>
                            <TableCell width="20%" name="board_no" value={this.props.board_no}> {this.props.board_no} </TableCell>
                            <TableCell width="20%" name="board_title" value={this.props.board_title}> {this.props.board_title} </TableCell>
                            <TableCell width="20%" name="board_content" value={this.props.board_content}> {this.props.board_content} </TableCell>
                            <TableCell width="20%" name="board_email" value={this.props.board_email}> {this.props.board_email} </TableCell>
                            <TableCell width="10%" name="board_regdate">{moment(regdate).formate('YYYY-MM-DD hh:mm')} </TableCell>
                            <TableCell width="10%"><input type="button" class="btn" value="수정" onClick={displayUpdateClick}></input></TableCell>
                            <TableCell width="10%"><input type="button" class="btn" value="삭제" onClick={displayDeleteClick}></input></TableCell>
                        </TableRow>
                    </Table>

                    {/* <div className="list" style={{display: this.state.show_update}}>
                        <Table>


                        </Table>
                    </div> */}

                </form>
            </div>

        )                
    }
}
export default BoardList;
