import React, {Component} from 'react';
import {Table, TableCell, TableRow } from '@material-ui/core';
import {Input} from '@material-ui/core';
import "ka-table/style.css";
import moment from 'moment';  // 날짜 타입
import '../css/default.css';
import { Link, BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import Record from './Record';
import project from './projcet';

class BoardList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value : '',
            show_update: "none",
            show_delete: "none"
        }
    }

    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_update: "block",
            show_delete: "none" 
        });
    }   

    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_update: "none",
            show_delete: "block"
        });
    }

    // Input : @material-ui/core에서 제공하는 input bos로 라인형태로 제공
    // input : html 태그
    // <input type="hidden" name="board_no" value={this.props.board_no} />  => where절에서 key비교를 위해
    render() {
        let regdate = this.props.writeDate;
        return(
            <Router>
                <div class="ka">
                    <form action="/api/boardUpdate" method="POST">
                        <input type="hidden" name="recordCode" value={this.props.recordCode} />
                            <tbody class="ka-tbody">
                                <tr class="ka-tr ka-row">
                                    <td class="ka-cell">
                                        <div class="ka-cell-text">
                                            <button>상세</button>
                                        </div>
                                    </td>
                                    <td class="ka-cell">
                                        <div class="ka-cell-text">test1</div>
                                    </td>
                                    <td class="ka-cell">
                                        <div class="ka-cell-text">test2</div>
                                    </td>
                                    <td class="ka-cell">
                                        <div class="ka-cell-text">test3</div>
                                    </td>
                                    <td class="ka-cell">
                                        <div class="ka-cell-text">test4</div>
                                    </td>

                                </tr>    
                            </tbody>  
                        {/* <Table>
                            <TableRow>
                                <TableCell width="20%" name="recordCode" value={this.props.recordCode}> {this.props.recordCode} </TableCell>
                                <TableCell width="20%" name="dept" value={this.props.dept}> {this.props.dept} </TableCell>
                                <TableCell width="20%" name="name" value={this.props.name}> {this.props.name} </TableCell>
                                <TableCell width="20%" name="description" value={this.props.description}> {this.props.description} </TableCell>
                                <TableCell width="20%" name="writeDate">{moment(regdate).format('hh:mm:ss')} </TableCell>
                                <TableCell width="10%"><Link to="/project">보기</Link></TableCell>
                             </TableRow>   
                              <TableRow>
                                <TableCell>
                                    <Route exact path="/project" component={project} />
                                </TableCell>                                
                                <TableCell width="10%"><input type="button" class="btn" value="수정" onClick={this.displayUpdateClick}></input></TableCell>
                                <TableCell width="10%"><input type="button" class="btn" value="삭제" onClick={this.displayDeleteClick}></input></TableCell>
                            </TableRow>
                        </Table> */}

                        {/* 수정화면 ==> where절에서 key비교를 위해 <input type="hidden" name="board_no" value={this.props.board_no} />
                        <div className="list" style={{display: this.state.show_update}}>
                            <Table>
                                <TableRow>
                                    <TableCell>
                                        <Input type="text" name="board_title" placeholder={this.props.board_title} />
                                    </TableCell>
                                    <TableCell>
                                        <Input type="area" name="board_content" placeholder={this.props.board_content} />
                                    </TableCell>
                                    <TableCell>
                                        <Input type="email" name="board_email" placeholder={this.props.board_email} />
                                    </TableCell>
                                    <TableCell>
                                        {moment(this.props.board_regdate).format('YYYY-MM-DD hh:mm')}
                                    </TableCell>
                                    <TableCell>
                                        <input class="btn" type="submit" value="수정" color="primary" />
                                    </TableCell>
                                </TableRow>
                            </Table>
                        </div>  */}
                    </form>
                    
                        {/* 삭제 화면
                    <div className="delete" style={{display: this.state.show_delete}}>
                        <form action="/api/boardDelete" method="POST">
                            <input type="hidden" name="board_no" value={this.props.board_no} />
                            <Table>
                                <TableRow>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell>
                                        <span align="center">삭제하시겠습니까?</span>
                                    </TableCell>
                                    <TableCell>
                                        <input class="btn" type="submit" value="삭제" color="primary" />
                                    </TableCell>
                                </TableRow>
                            </Table>
                        </form>
                    </div> */}
                </div> 
            </Router>
        )                
    }
}
export default BoardList;
