import React, {Component} from 'react';
import { Input, Table, TableCell, TableRow } from '@material-ui/core';
import moment from 'moment';  // 날짜 타입
import '../css/default.css';

class BoardList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value : '',
            show_update: "none",
            show_delete: "none"
        }
    }

    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_update: "block",
            show_delete: "none" 
        });
    }   

    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_update: "none",
            show_delete: "block"
        });
    }
    render() {
        let regdate = this.props.board_regdate;
        return(
            <div>
                <form action="/api/boardUpdate" method="POST">
                    <input type="hidden" name="board_no" value={this.props.board_no} />  
                    <Table>
                        <TableRow>
                            <TableCell width="20%" name="board_no" value={this.props.board_no}> {this.props.board_no} </TableCell>
                            <TableCell width="20%" name="board_title" value={this.props.board_title}> {this.props.board_title} </TableCell>
                            <TableCell width="20%" name="board_content" value={this.props.board_content}> {this.props.board_content} </TableCell>
                            <TableCell width="20%" name="board_email" value={this.props.board_email}> {this.props.board_email} </TableCell>
                            <TableCell width="10%" name="board_regdate">{moment(regdate).format('YYYY-MM-DD hh:mm')} </TableCell>
                            <TableCell width="10%"><input type="button" class="btn" value="수정" onClick={this.displayUpdateClick}></input></TableCell>
                            <TableCell width="10%"><input type="button" class="btn" value="삭제" onClick={this.displayDeleteClick}></input></TableCell>
                        </TableRow>
                    </Table>

                    {/* 수정화면 ==> where절에서 key비교를 위해 <input type="hidden" name="board_no" value={this.props.board_no} /> */}
                    <div className="list" style={{display: this.state.show_update}}>
                        <Table>
                            <TableRow>
                                <TableCell>
                                    <Input type="text" name="board_title" placeholder={this.props.board_title} />
                                </TableCell>
                                <TableCell>
                                    <Input type="area" name="board_content" placeholder={this.props.board_content} />
                                </TableCell>
                                <TableCell>
                                    <Input type="email" name="board_email" placeholder={this.props.board_email} />
                                </TableCell>
                                <TableCell>
                                    {moment(this.props.board_regdate).format('YYYY-MM-DD hh:mm')}
                                </TableCell>
                                <TableCell>
                                    <input class="btn" type="submit" value="수정" color="primary" />
                                </TableCell>
                            </TableRow>
                        </Table>
                    </div> 

                </form>
                    {/* 삭제 화면 */}



            </div>

        )                
    }
}
export default BoardList;
